import Dexie from 'dexie'
import React, { Component } from 'react'
import QuestionBox  from './QuestionBox'
import Scoresheet from './Scoresheet'


export class Quiz extends Component {
        
    state = {
        questionBank: [],
        currentIndex: 0,
        userAnswer: null,
        score:0
    }

    //Reads the question from IndexedDB and load them into the questionBank state
    loadQuestions = () => {
        let arr = [];
        var db = new Dexie('questionsDB');
        db.version(2).stores({
            questions: 'id,question, option1, option2, option3, option4, answer'
        });

        db.open().catch(function(){
            console.log("Error Occurred: ")
        });

        db.questions.each((item) => arr.push(item))
        .then(() => this.setState({questionBank: arr}));
    }


    componentDidMount(){
        this.loadQuestions()
    }

    retakeQuiz = () => {
        this.setState({
            currentIndex: 0
        })
    }


    nextQuestionHander = () => {
        this.setState({
            currentIndex:  this.state.currentIndex + 1
        })
        console.log(this.state.currentIndex)
        console.log("Total Questions " + this.state.questionBank.length)
    }

    render() {
        const {currentIndex, questionBank} = this.state
        var currentQuestion = questionBank[currentIndex]

        if(currentIndex <= questionBank.length -1) {
            return (
                <>
                <div>
                    <QuestionBox currentQuestion = {currentQuestion}></QuestionBox>
                </div>
                <button onClick={this.nextQuestionHander}>Next</button>
                </>
            )
        }
        else {
            return (
                <div>
                <Scoresheet></Scoresheet>
                <button onClick={this.retakeQuiz} >Retake Quiz</button>
                </div>
            )
        }
    }
}

export default Quiz
