import React, { Component } from 'react'
import "./styles.css"

export class QuestionBox extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             newQuiz: null,
             answered: false,
             selectedButton:null,
             currentClass: "options"
        }
        this.sendAnswer = this.sendAnswer.bind(this)
    }

    sendAnswer = (event) => {           
        if(this.state.answered === false){
            event.target.className = "selected"           
            this.setState({
                answered: true,
                selectedButton: event.target,
            })
            this.props.checkAnswer(event.target.innerHTML);
        }
    }


    render() {   
        const{currentClass} = this.state
        if(this.props.currentQuestion !== undefined) {
            var quiz = this.props.currentQuestion
            return (
                <div className="questionBox" >
                <h1> {quiz.question}</h1>
                <ol type='A'>
                    <li onClick={this.sendAnswer} className={currentClass}>{quiz.option1}</li>
                    <li onClick={this.sendAnswer} className={currentClass}>{quiz.option2}</li>
                    <li onClick={this.sendAnswer} className={currentClass}>{quiz.option3}</li>
                    <li onClick={this.sendAnswer} className={currentClass}>{quiz.option4}</li>
                </ol>  
                </div>
            )
        }
        else {
            return null
        }
    }
}

export default QuestionBox
