import React, { Component } from 'react'
import Dexie from 'dexie'
import QuestionManager from './QuestionManager'

export class Game extends Component {

    constructor(props) {
        super(props)   
        this.state = {
             numberOfQuestions:0,
             gotoQuestionManager: false
        }
    }

    componentWillMount(){
        let count = 0;
        var db = new Dexie('questionsDB');
        db.version(2).stores({
            questions: 'id,question, option1, option2, option3, option4, answer'
        });
        db.open().catch(function(){
            console.log("Error Occurred: ")
        });

        db.questions.each((item) => count = count+1)
        .then(() => {
            this.setState({numberOfQuestions: count})
        });
    }

    manageQuestions(){
        this.setState({
            gotoQuestionManager: true
        })
    }


    render() {

        if(this.state.gotoQuestionManager == true){
            return (
                <QuestionManager></QuestionManager>
            )
        }

        return (
            <div>
                Number of Question: {this.state.numberOfQuestions}<br />

                <button onClick={this.manageQuestions}>Manage Questions</button>

                <button onClick={this.startQuiz}>Start Quiz</button>
            </div>
        )
    }
}

export default Game
