import Dexie from 'dexie'
import React, { Component } from 'react'
import QuestionBox  from './QuestionBox'
import {QuizData}  from './QuizData'


export class Quiz extends Component {
        
    state = {
        questionBank: [],
        currentIndex: 0
    }

    loadQuestions = () => {
        let arr = [];
        var db = new Dexie('questionsDB');
        db.version(2).stores({
            questions: 'id,question, option1, option2, option3, option4, answer'
        });

        db.open().catch(function(){
            console.log("Error Occurred: ")
        });

        db.questions.each((item) => arr.push(item))
        .then(() => this.setState({questionBank: arr}));
    }

    componentDidMount(){
        this.loadQuestions();
    }

   //Component that holds the current quiz
   showOneQuestion = () => {
    const {currentIndex} = this.state //get the current question index
    this.setState(() => {
        return {
           question: QuizData[currentIndex].question
            // options : QuizData[currentIndex].options,
            // answer: QuizData[currentIndex].answer          
        }
    }
    )
}

    render() {
        const {question} = this.state
        return (
            <div>
                <QuestionBox ></QuestionBox>
            </div>
        )
    }
}

export default Quiz
