import Dexie from 'dexie'

import React, { Component } from 'react'

export class AddMore extends Component {

    state = {
        questionBank: []
    }

    loadQuestions = () => {
        let arr = [];
        var db = new Dexie('questionsDB');
        db.version(2).stores({
            questions: 'id,question, option1, option2, option3, option4, answer'
        });

        db.open().catch(function(){
            console.log("Error Occurred: ")
        });

        db.questions.each((item) => arr.push(item))
        .then(() => this.setState({questionBank: arr}));
    }
        
      componentDidMount(){
          this.loadQuestions();
      }

    render() {
        var list = this.state.questionBank.map(result =>{
            return <li key={result.id}>{result.question}</li>
        });
        return (
            <div>
                <ul>
                    {list}
                </ul>
            </div>
        )
    }
}

export default AddMore
