import React, { Component } from 'react'
import "./styles.css"

export class QuestionBox extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             answered: null,
             currentStyle: "options"
        }
        this.sendAnswer = this.sendAnswer.bind(this)
    }

    sendAnswer = (event) => {      
        console.log(event.target.className)
        if(this.state.answered === false){
            event.target.className = `${this.state.currentStyle} "selected"`      
            this.setState({
                answered: true
            })
            this.props.checkAnswer(event.target.innerHTML);
        }
    }


    render() {   
        const{currentStyle, answered} = this.state
        console.log("CurrentStyle state: " + currentStyle)
        console.log("Answered state: " + this.props.answered)
        if(this.props.currentQuestion !== undefined) {
            var quiz = this.props.currentQuestion
            return (
                <div className="questionBox" >
                <h1> {quiz.question}</h1>
                <ol type='A'>
                    <li onClick={this.sendAnswer} className={`currentStyle ${answered}`}>{quiz.option1}</li>
                    <li onClick={this.sendAnswer} className="options ">{quiz.option2}</li>
                    <li onClick={this.sendAnswer} className="options ">{quiz.option3}</li>
                    <li onClick={this.sendAnswer} className="options ">{quiz.option4}</li>
                </ol>  
                </div>
            )
        }
        else {
            return null
        }
    }
}

export default QuestionBox
