import React, { Component } from 'react'
import Dexie from 'dexie'
import uuid from 'react-uuid'

export class Form extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             question: '',
             option1: '',
             option2: '',
             option3: '',
             option4: '',
             answer: ''
        }
    }

    handleChange = (event) => {
        var inputName = event.target.name;

        if(inputName === 'txtQuestion'){
            this.setState({question: event.target.value})
        }
        else if(inputName === 'txtOption1') {
            this.setState({option1: event.target.value})
        }
        else if(inputName === 'txtOption2') {
            this.setState({option2: event.target.value})
        }
        else if(inputName === 'txtOption3') {
            this.setState({option3: event.target.value})
        }
        else if(inputName === 'txtOption4') {
            this.setState({option4: event.target.value})
        }
        else if(inputName === 'txtAnswer') {
            this.setState({answer: event.target.value})
        }
    }

    testUUID = () => {
        alert(uuid())
    }

    insertData = () => {
        var db = new Dexie('questionsDB');
        db.version(2).stores({
            questions: 'id, question, option1, option2, option3, option4, answer'
        })

        db.open().catch(function(){
            console.log("Error Occurred: ")
        });

        db.questions.put({
            id: "2",
            question:{question}, 
            option1: {option1},
            option2: {option2},
            option3: {option3},
            option4: {option4},
            answer: {answer}
        })
        .then(function(){   
            console.log("Question was added!")       
        })
  };
    

    render() {
        return (
            
              <form>
                <label>Enter Question</label>
                <textarea name="txtQuestion" onChange={this.handleChange} rows="2" cols="50" value={this.state.question}></textarea>
                <hr></hr>
                Answers
                <p>
                    <label>Option 1: </label>
                    <input name="txtOption1" onChange={this.handleChange} type="text" value={this.state.option1}></input>
                </p>
                <p>
                    <label>Option 2: </label>
                    <input name="txtOption2" onChange={this.handleChange} type="text" value={this.state.option2}></input>
                </p>
                <p>
                    <label>Option 3: </label>
                    <input name="txtOption3" onChange={this.handleChange} type="text" value={this.state.option3}></input>
                </p>
                <p>
                    <label>Option 4: </label>
                    <input name="txtOption4" onChange={this.handleChange} type="text" value={this.state.option4}></input>
                </p>  
                <hr />
                <p>
                    <label>Answer: </label>
                    <input name="txtAnswer" onChange={this.handleChange} type="text" value={this.state.answer}></input>
                </p>  
                <hr />                              
                <p>
                    <button onClick={this.testUUID}>Add Question</button></p>                 
            </form>
        )
    }
}

export default Form
