import React, { Component } from 'react'
import "./styles.css"

export class QuestionBox extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             answered: false,
        }

        this.iRef = React.createRef()

        this.sendAnswer = this.sendAnswer.bind(this)
    }

    sendAnswer = (event) => {      
        if(this.state.answered === false){
            event.target.className = "options selected"    
            this.setState({
                answered: true,
                iRef : event.target
            })
            this.props.checkAnswer(event.target.innerHTML);
        }
    }

    resetStyles = () => {
        console.log(this.iRef.current==null? null: this.iRef.current.innerHTML)
    }


    render() {   

        this.resetStyles();

        if(this.props.currentQuestion !== undefined) {
            var quiz = this.props.currentQuestion
            return (
                <div className="questionBox" >
                <h1> {quiz.question}</h1>
                <ol type='A'>
                    <li onClick={this.sendAnswer} ref={this.iRef} className="options">{quiz.option1}</li>
                    <li onClick={this.sendAnswer} ref={this.iRef} className="options ">{quiz.option2}</li>
                    <li onClick={this.sendAnswer} ref={this.iRef} className="options ">{quiz.option3}</li>
                    <li onClick={this.sendAnswer} ref={this.iRef} className="options ">{quiz.option4}</li>
                </ol>  
                </div>
            )
        }
        else {
            return null
        }
    }
}

export default QuestionBox
