import Dexie from 'dexie'
import React, { Component } from 'react'
import QuestionBox  from './QuestionBox'
import Scoresheet from './Scoresheet'
import "./styles.css"


export class Quiz extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
            questionBank: [],
            currentIndex: 0,
            userAnswer: null,
            score:0,
            disabled: true,
            answered: false
        }

        this.checkAnswer = this.checkAnswer.bind(this)
        this.answerIsCorrect = this.answerIsCorrect.bind(this)
    }
    


    //Reads the question from IndexedDB and load them into the questionBank state
    loadQuestions = () => {
        let arr = [];
        var db = new Dexie('questionsDB');
        db.version(2).stores({
            questions: 'id,question, option1, option2, option3, option4, answer'
        });

        db.open().catch(function(){
            console.log("Error Occurred: ")
        });

        db.questions.each((item) => arr.push(item))
        .then(() => this.setState({questionBank: arr}));
    }


    componentDidMount(){
        this.loadQuestions()
    }

    retakeQuiz = () => {
        this.setState({
            currentIndex: 0,
            score:0
        })
    }


    //Increment the currentIndex
    nextQuestionHander = () => {
        const{answered} = this.state
        console.log(answered)
        this.setState({
            currentIndex:  this.state.currentIndex + 1,
            disabled:true,
            answered: false
        })
    }

    answerIsCorrect(userAnswer){
        const {currentIndex, questionBank} = this.state
        if(userAnswer === questionBank[currentIndex].answer) {
            return true
        } else {return false}
    }

    //Increment score if answer is correct and enable Next button
    checkAnswer = (event) =>{
        const {score, answered} = this.state
        if(answered === false) {
            console.log(answered)
            console.log(event.target.innerHTML)
            event.target.className = "options selected"
            if(this.answerIsCorrect(event.target.innerHTML)){
                this.setState({
                    score: score + 1,
                    answered: true
                })
            }

            this.setState({
                //userAnswer: selectedAnswer,
                disabled:false
            })
        }
    }

    render() {
        const {currentIndex, questionBank, score, answered} = this.state
        var currentQuestion = questionBank[currentIndex]

        if(currentIndex <= questionBank.length -1) {
            return (
                <div>                    
                    <div className="questionBox" >
                    <h1> {currentQuestion.question}</h1>
                    <ol type='A'>
                        <li onClick={this.checkAnswer} className="options">{currentQuestion.option1}</li>
                        <li onClick={this.checkAnswer} className="options ">{currentQuestion.option2}</li>
                        <li onClick={this.checkAnswer} className="options ">{currentQuestion.option3}</li>
                        <li onClick={this.checkAnswer} className="options ">{currentQuestion.option4}</li>
                    </ol>  
                    </div>
                    
                <button 
                onClick={this.nextQuestionHander} 
                disabled = {this.state.disabled}>Next</button>
                </div>
            )
        }
        else {
            return (
                <div>
                <Scoresheet score={score} ></Scoresheet>
                <button onClick={this.retakeQuiz} >Retake Quiz</button>
                </div>
            )
        }
    }
}

export default Quiz
