import Dexie from 'dexie'

import React, { Component } from 'react'
import Form from './Form'

export class AddMore extends Component {

    state = {
        questionBank: []
    }

    loadQuestions = () => {
        // Get data from the IndexedDB and display it
        var db = new Dexie('questionsDB');
        db.version(2).stores({
            questions: 'id,question, option1, option2, option3, option4, answer'
        })
        db.open().catch(function(){
            console.log("Error Occurred: ")
        });
        //   db.questions.get('Kindson').then(result => {
        //     this.setState({
        //         questionBank: result
        //     })
        // });
    }
    
    handleClick = () => {
            var db = new Dexie('questionsDB');
            db.version(2).stores({
                questions: 'id, question, option1, option2, option3, option4, answer'
            })
    
            db.open().catch(function(){
                console.log("Error Occurred: ")
            });
    
            db.questions.put({
                id: "2",
                question:'What is your name?', 
                option1: 'African',
                option2: 'African',
                option3: 'African',
                option4: 'African',
                answer: 'African'
            })
            .then(function(){   
                console.log("Question was added!")       
            })
      };
    
      componentDidMount(){
          this.loadQuestions();
      }

    render() {
        return (
            <div>
            This is QuestionManager
            <hr></hr>
            <Form />
        </div>
        )
    }
}

export default AddMore
