import React, {useState} from 'react'
import './styles.css'
import Header from './Header'
import Start from './Start'
import Quiz from './Quiz'


function Scoresheet(props) {
    const [goHome, setGoHome] = useState(false)
    const [restart, setRestart] = useState(false)

    if(goHome === true){
        return (
        <div>
            <Start></Start>
        </div>
        )
    }
    if(restart === true){
        <div>
        <Quiz></Quiz>
         </div> 
    }
    return (
        <div>
            <Header username={props.username} />
            <div className="container small">
                <div className="scoresheet">
                    <h1>Quiz Successfully Completed</h1><br />
                    <h3>Your score is {props.score}</h3>
                    <h4>That is {props.score / props.totalQuestions * 100}% </h4>
                </div>  
                <button onClick={() => setGoHome(true)}>Home</button>     
                <button onClick={() => setRestart(true)}>Restart Quiz</button>   
            </div>
        </div>
    )
}

export default Scoresheet
