import React, { Component } from 'react'
import Dexie from 'dexie'

export class Game extends Component {

    constructor(props) {
        super(props)   
        this.state = {
             numberOfQuestions:0
        }
    }
    

    noQuestionAvailable =() => {
        console.log(this.state.numberOfQuestions)
    }

    componentWillMount(){
        let count = 0;
        var db = new Dexie('questionsDB');
        db.version(2).stores({
            questions: 'id,question, option1, option2, option3, option4, answer'
        });
        db.open().catch(function(){
            console.log("Error Occurred: ")
        });

        db.questions.each((item) => count = count+1)
        .then(() => {
            this.setState({numberOfQuestions: count})
        });
    }


    render() {

        return (
            <div>
                <button onClick ={this.noQuestionAvailable}>Check</button>
                
            </div>
        )
    }
}

export default Game
