import React, { Component } from 'react'



export class QuestionBox extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             newQuiz: null
        }
    }

    render() {
        console.log(this.props.currentQuestion) 
        if(this.props.currentQuestion !== undefined) {
            var quiz = this.props.currentQuestion
            return (
                <div><h1> {quiz.question}</h1>
                <ol type='A'>
                    <li onClick ={checkAnswer} className={`ui floating message`}>{quiz.option1}</li>
                    <li>{quiz.option2}</li>
                    <li>{quiz.option3}</li>
                    <li>{quiz.option4}</li>
                </ol>  
            </div>
            )
        }
        else {
            return null
        }
    }
}

export default QuestionBox
