import Dexie from 'dexie'
import React, { Component } from 'react'
import Scoresheet from './Scoresheet'
import "./styles.css"


export class Quiz extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
            questionBank: [],
            currentIndex: 0,
            userAnswer: null,
            score:0,
            disabled: true,
            disabledOptions: false,
            option1: false,
            option2: false,
            option3: false,
            option4: false
        }

        this.checkAnswer = this.checkAnswer.bind(this)
        this.answerIsCorrect = this.answerIsCorrect.bind(this)
    }
    
    //Reads the question from IndexedDB and load them into the questionBank state
    loadQuestions = () => {
        let arr = [];
        var db = new Dexie('questionsDB');
        db.version(2).stores({
            questions: 'id,question, option1, option2, option3, option4, answer'
        });

        db.open().catch(function(){
            console.log("Error Occurred: ")
        });

        db.questions.each((item) => arr.push(item))
        .then(() => this.setState({questionBank: arr}));
    }


    componentDidMount(){
        this.loadQuestions()
    }

    retakeQuiz = () => {
        this.setState({
            currentIndex: 0,
            score:0
        })
    }

    //Increment the currentIndex
    nextQuestionHander = () => {
        this.setState({
            currentIndex:  this.state.currentIndex + 1,
            disabled:true,
            disabledOptions: false,
            option1: false,
            option2: false,
            option3: false,
            option4: false
        })
    }

    answerIsCorrect(userAnswer){
        const {currentIndex, questionBank} = this.state
        if(userAnswer === questionBank[currentIndex].answer) {
            return true
        } else {return false}
    }

    //Increment score if answer is correct and enable Next button
    checkAnswer = () =>{
        const {score, userAnswer} = this.state
            if(this.answerIsCorrect(userAnswer)){
                this.setState({
                    score: score + 1
                })
            }

            this.setState({
                disabledOptions:true,
                disabled: false
            })
    }
    
    setUserAnswer = (event) => {
        if(event.target.id === "o1") { 
            this.setState({option1: event.target.checked })
        }
        else if(event.target.id ==="o2") {
            this.setState({option2: event.target.checked})
        }
        else if(event.target.id ==="o3") {
            this.setState({option3: event.target.checked})
        }
        else if(event.target.id ==="o4") {
            this.setState({option4: event.target.checked})
        }
    }

    render() {
        const {currentIndex, questionBank, score} = this.state
        var currentQuestion = questionBank[currentIndex]      

        if(currentIndex <= questionBank.length -1) {
            return (
                <div>                    
                    <div className="questionBox" >
                    <h1> {currentQuestion.question}</h1>
                    <fieldset disabled={this.state.disabledOptions}>
                        <input id="o1" onChange={this.setUserAnswer} type="radio" name="group1" value={currentQuestion.option1} checked={this.state.option1}/> {currentQuestion.option1}<br />
                        <input id="o2" onChange={this.setUserAnswer} type="radio" name="group1" value={currentQuestion.option2} checked={this.state.option2}/> {currentQuestion.option2}<br />
                        <input id="o3" onChange={this.setUserAnswer} type="radio" name="group1" value={currentQuestion.option3} checked={this.state.option3}/> {currentQuestion.option3}<br />
                        <input id="o4" onChange={this.setUserAnswer} type="radio" name="group1" value={currentQuestion.option4} checked={this.state.option4}/> {currentQuestion.option4}<br />
                    </fieldset> 
                    <button onClick={this.checkAnswer}>Check Answer</button><br />
                    </div>
                    
                <button 
                onClick={this.nextQuestionHander} 
                disabled = {this.state.disabled}>Next</button>
                </div>
            )
        }
        else {
            return (
                <div>
                <Scoresheet score={score} ></Scoresheet>
                <button onClick={this.retakeQuiz} >Retake Quiz</button>
                </div>
            )
        }
    }
}

export default Quiz
