import Dexie from 'dexie'

import React, { Component } from 'react'
import Form from './Form'

export class QuestionManager extends Component {

    state = {
        questionBank: []
    }

    loadQuestions = () => {
        let arr = [];
        var db = new Dexie('questionsDB');
        db.version(2).stores({
            questions: 'id,question, option1, option2, option3, option4, answer'
        });

        db.open().catch(function(){
            console.log("Error Occurred: ")
        });

        db.questions.each((item) => arr.push(item))
        .then(() => this.setState({questionBank: arr}));
    }

    deleteQuestion = (event) => {
        var db = new Dexie('questionsDB');

        db.version(2).stores({
            questions: 'id,question, option1, option2, option3, option4, answer'
        });

        var clickedId = event.target.id;

        db.open().catch(function(){
            console.log("Error Occurred: ")
        });

        db.questions.delete(clickedId).then(() =>{
            this.loadQuestions();
        });
    }
        
      componentDidMount(){
        console.log(this.props.username)
      }

    render() {
        this.loadQuestions()

        var list = this.state.questionBank.map(result =>{
            return (
                <div key={result.id}><h4>{result.question}</h4> <button id={result.id} onClick={this.deleteQuestion}>Delete</button>
                        <li className="inlineList">{result.option1}</li>
                        <li className="inlineList">{result.option2}</li>
                        <li className="inlineList">{result.option3}</li>
                        <li className="inlineList">{result.option4}</li>
                </div>
            )
        });


        return (
            <div>                
                {list}
              <hr />  
              <Form></Form>
            </div>
        )
    }
}

export default QuestionManager
