import Dexie from 'dexie'

import React, { Component } from 'react'

export class AddMore extends Component {

    state = {
        questionBank: []
    }

    loadQuestions = () => {
        // Get data from the IndexedDB and display it
        var db = new Dexie('questionsDB');
        db.version(2).stores({
            questions: 'id,question, option1, option2, option3, option4, answer'
        })
        db.open().catch(function(){
            console.log("Error Occurred: ")
        });
          db.questions.get('Kindson').then(result => {
            this.setState({
                questionBank: result
            })
        });
    }
    
    
      componentDidMount(){
          this.loadQuestions();
      }

    render() {
        return (
            <div>
            <hr></hr>
        </div>
        )
    }
}

export default AddMore
