import Dexie from 'dexie'

import React, { Component } from 'react'

export class QuestionManager extends Component {

    state = {
        questionBank: []
    }

    loadQuestions = () => {
        let arr = [];
        var db = new Dexie('questionsDB');
        db.version(2).stores({
            questions: 'id,question, option1, option2, option3, option4, answer'
        });

        db.open().catch(function(){
            console.log("Error Occurred: ")
        });

        db.questions.each((item) => arr.push(item))
        .then(() => this.setState({questionBank: arr}));
    }

    deleteQuestion = (event) => {
        var db = new Dexie('questionsDB');

        db.version(2).stores({
            questions: 'id,question, option1, option2, option3, option4, answer'
        });

        var clickedId = event.target.id;

        db.open().catch(function(){
            console.log("Error Occurred: ")
        });

        db.questions.delete(clickedId).then(() =>{
            this.loadQuestions();
        });


    }
        
      componentDidMount(){
        //   this.loadQuestions();
      }

    render() {
        this.loadQuestions()

        var list = this.state.questionBank.map(result =>{
            return (
                <div key={result.id}>{result.question} <button id={result.id} onClick={this.deleteQuestion}>Delete</button>
                    <ol type='A'>
                        <li>{result.option1}</li>
                        <li>{result.option2}</li>
                        <li>{result.option3}</li>
                        <li>{result.option4}</li>

                    </ol>
                </div>
            )
        });


        return (
            <div>
                
                    {list}
                
            </div>
        )
    }
}
